import NewElementMarker from "./components/NewElementMarker"
import ProductCartImg from "./ProductCartImg"
import ProductCartButton from "./ProductCartButton"
import { useState } from "react"

const ProductCart = ({title, model, previousPrice, price, newElement}) => {

    const [state, setState] = useState(false)

    const rr1 = function () {
        setState(true) 
    }

    const rr2 = function () {
        setState(false) 
    }

    return (
        <li className="products-catalog__item">
            {newElement === true ? <NewElementMarker></NewElementMarker> : ""}
            <div onMouseEnter={rr1} onMouseLeave={rr2} className="products-catalog__top">
                {state === true ? <ProductCartButton></ProductCartButton> : <ProductCartImg title={title} model={model}></ProductCartImg>}
                {state === true ? <ProductCartImg title={title} model={model}></ProductCartImg> : <ProductCartButton></ProductCartButton>}
            </div>
            <h3 className="products-catalog__title">{title}<br />{model}</h3>
            <s className="products-catalog__previous-price">{previousPrice} Р.</s>
            <a className="products-catalog__price" href="link">{price} Р.</a>
        </li>
    )
}

export default ProductCart
