import { useDispatch } from "react-redux"
import { addTodo } from "../../store/todoCart"

const ProductCartButton = (id) => {
    const dispatch = useDispatch()
    const addCart = () => dispatch(addTodo({id}))

    return (
        <div className="products-catalog__buttons">
            <button type="button" className="products-catalod__buy-btn">Купить</button>
            <button id={id} onClick={() => addCart()} type="button" className="products-catalod__in-cart-btn">В корзину</button>
        </div>
    )
}

export default ProductCartButton
