import { useState } from "react"
import { useDispatch } from "react-redux"
import { addTodo } from "../../store/todoCart"

const ProductCart = ({ title, model, previousPrice, price, id }) => {

    const [open, setOpen] = useState(false)
    const [inCart, setInCart] = useState(false)
    const dispatch = useDispatch()
    const addCart = () => {
        setInCart(true)
        dispatch(addTodo({ id }))
    }

    return (
        <li className="products-catalog__item">
            <div className="products-catalog__top" onMouseEnter={() => setOpen(true)} onMouseLeave={() => setOpen(false)}>
                <img className={`products-catalog__img ${open === true ? "visually-hidden" : ""}`} alt={model} />
                <div className={`products-catalog__buttons ${open === false ? "visually-hidden" : ""}`}>
                    <button type="button" className="products-catalod__buy-btn">Купить</button>
                    <button type="button" onClick={addCart} className="products-catalod__in-cart-btn">{inCart ? "Уже в корзине" : "В корзину"}</button>
                </div>
            </div>
            <h3 className="products-catalog__title">{title}<br />{model}</h3>
            <s className="products-catalog__previous-price">{previousPrice} Р.</s>
            <a className="products-catalog__price" href="link">{price} Р.</a>
        </li>
    )
}

export default ProductCart
