import CreditPage from "./CreditPage"
import WarrantyPage from "./WarrantyPage"
import DeliveryPage from "./DeliveriPage"
import { useState } from "react"
import { createUseStyles } from "react-jss";


const ServicesSlider = () => {

    const styles = createUseStyles({
        servicesInner: {
            display: "flex",
            marginTop: "70px"
        },
        servicesLeft: {
            position: "relative"
        }
    });

    const classes = styles()


    const [page, setPage] = useState("creditPage")

    return (
        <div className={classes.servicesInner}>
            <div className={classes.servicesLeft}>
                <ul className="services-button-list">
                    <li className="turin">
                        <button className="services-list-btn-item" type="button" onClick={() => setPage("creditPage")}>Доставка</button>
                    </li>
                    <li className="turin-button-item">
                        <button className="services-list-btn-item" type="button" onClick={() => setPage("warrantyPage")}>Гарантия</button>
                    </li>
                    <li className="services-button-item">
                        <button className="services-list-btn-item" type="button" onClick={() => setPage("deliveryPage")}>Кредит</button>
                    </li>
                </ul>
                <div className="services-left-background-image"></div>
            </div>
            <div className="services-right">
                <div className="services-list">
                    {page === "creditPage" && <CreditPage></CreditPage>}
                    {page === "warrantyPage" && <WarrantyPage></WarrantyPage>}
                    {page === "deliveryPage" && <DeliveryPage></DeliveryPage>}
                </div>
            </div>
        </div>
    )
}

export default ServicesSlider
