import { useDispatch } from "react-redux"
import { addTodo } from "../../store/todoCart"
import { useState } from "react"

const ProductCartButton = (id) => {

    const [state, setState] = useState("В корзину")
    const dispatch = useDispatch()
    const addCart = () => {
        dispatch(addTodo({id}))
        setState("Уже в корзине")
    }

    return (
        <div className="products-catalog__buttons">
            <button type="button" className="products-catalod__buy-btn">Купить</button>
            <button id={id} onClick={() => addCart()} type="button" className="products-catalod__in-cart-btn">{state}</button>
        </div>
    )
}

export default ProductCartButton
