import CreditPage from "./CreditPage"
import WarrantyPage from "./WarrantyPage"
import DeliveryPage from "./DeliveriPage"
import { useState } from "react"
import { createUseStyles } from "react-jss";
import { colors } from "../../../style/globalStyle";


const ServicesSlider = () => {

    const styles = createUseStyles({
        servicesInner: {
            display: "flex",
            marginTop: "70px"
        },
        servicesLeft: {
            position: "relative"
        },
        buttonList: {
            width: "240px",
            flexShrink: "0",
            display: "flex",
            flexDirection: "column",
            justifyContent: "flex-start"
        },
        buttonItem: {
            backgroundColor: colors.darkBlue,
            border: "0",
            justifyContent: "flex-start",
            display: "flex",
            paddingLeft: "22px",
            paddingTop: "12px",
            paddingBottom: "12px",
            width: "100%",
            fontFamily: "Cuprum",
            fontSize: "21px",
            lineHeight: "30px",
            color: colors.white,
            borderTop: "1px solid #293449",
            borderBottom: "1px solid #293449",
            "&:hover": {
                backgroundColor: colors.blackBlue
            },
            "&:active": {
                backgroundColor: colors.white,
                color: colors.darkBlue,
            }
        },
        shadowImage: {
            backgroundImage: "url(/images/shadow.png)",
            position: "absolute",
            width: "10px",
            height: "280px",
            left: "calc(100% - 10px)",
            top: "-52px"
        }
    });

    const classes = styles()


    const [page, setPage] = useState("creditPage")

    return (
        <div className={classes.servicesInner}>
            <div className={classes.servicesLeft}>
                <ul className={classes.buttonList}>
                    <li>
                        <button className={classes.buttonItem} type="button" onClick={() => setPage("creditPage")}>Доставка</button>
                    </li>
                    <li>
                        <button className={classes.buttonItem} type="button" onClick={() => setPage("warrantyPage")}>Гарантия</button>
                    </li>
                    <li>
                        <button className={classes.buttonItem} type="button" onClick={() => setPage("deliveryPage")}>Кредит</button>
                    </li>
                </ul>
                <div className={classes.shadowImage}></div>
            </div>
            <div className="services-right">
                <div className="services-list">
                    {page === "creditPage" && <CreditPage></CreditPage>}
                    {page === "warrantyPage" && <WarrantyPage></WarrantyPage>}
                    {page === "deliveryPage" && <DeliveryPage></DeliveryPage>}
                </div>
            </div>
        </div>
    )
}

export default ServicesSlider
