import { createSlice } from "@reduxjs/toolkit";

const todoCart = createSlice({
    name: "todos",
    initialState: {
        todos: []
    },
    reducers: {
        addTodo(state, action) {


            state.todos.push({
                id: action.payload.id
            })

            console.log(state)
        }
    }
})

export const {addTodo} = todoCart.actions
export default todoCart.reducer
