import Perforators from "./Perforators"
import Drills from "./Drills"
import { useState } from "react"

const Slider = () => {

    const [state, setState] = useState(true)

    const slideImg = function () {
        setState(!state)
        if (state === true) {
            return state
        } else {
            return state
        }
    }

    return (
        <li className="promo-elements--boers slider">
            {state === false ? <Perforators></Perforators> : <Drills></Drills>}
            <div className="slider__btn-box">
                <button onClick={slideImg} className="slider__btn--left"></button>
                <button onClick={slideImg} className="slider__btn--right"></button>
            </div>
            <div className="slider__radio-box">
                <div className="slider__radio-inner">
                    <input className="slider__radio" type="radio" id="perforators"
                        name="perforators"/>
                    <input className="slider__radio" type="radio" id="drills" name="drills"/>
                </div>
            </div>
        </li>
    )
}

export default Slider
