import { createSlice } from "@reduxjs/toolkit";

const todoCart = createSlice({
    name: "todos",
    initialState: {
        todos: []
    },
    reducers: {
        addTodo(state, action) {

            console.log(action)

            state.todos.push({
                id: action.payload.text
            })
        }
    }
})

export const {addTodo} = todoCart.actions
export default todoCart.reducer
